import React , {Component} from 'react';
import CardList from './CardList';
import Scroll from './Scroll';
import SearchBox from './SearchBox';
import './App.css';
// import {robots} from './robots';



class App extends Component {    //Create a constructor function in the App component and add two properties

   constructor(){
	 super()
	 this.state = {
		robots: '',
		searchfield: ''
	 }
   }

   componentDidMount() {
   	fetch('https://jsonplaceholder.typicode.com/users')
	   	.then(response => response.json())
	   	.then(users => this.setState({robots : users}));

   }

   onSearchChange= (event)=> {

	this.setState({searchfield: event.target.value})  //setState is to change the value in the state object.In this case, searchfield will change


}
   // as a method inside class, no const 


//const {robots, searchfield} = this.state;  //destructoring 



render() {

			const filteredRobots = this.state.robots.filter(robot =>{
			  return robot.name.toLowerCase().includes(this.state.searchfield.toLowerCase()) //use the robots and searchfield properties in render function
			}) 

       // if (robots.length === 0　) {
       // 	return <h1>Loading</h1>
       // } 

       // else {
			return (

				<div className = 'tc'>
					  <h1 >RobotFriends</h1>
					  <SearchBox searchChange = {this.onSearchChange}/>  
					  <Scroll>
					     <CardList robots = {filteredRobots} />
		              </Scroll>
				</div>


					);

              //}	
     }	

  }

export default App;
